import React, { useEffect, useState } from "react";
import axios from "axios";

// API Keys
const defaultKey = "88b2bb448dff5786abe11bb1a3cfa4fe";

function Home(){
    // Assigning this list to the returned value of useState
    const [weatherData, setWeatherData] = useState({});
    const [city, setCity] = useState("Honolulu");

    // useEffect is a hook function that calls itself, once its variables change
    useEffect(() => {
        axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${defaultKey}`)
          .then(function (response) {
            // Logs in the success
            console.log(response);
            setWeatherData(response.data); // ???
          })
          .catch(function (error) {
            // Logs in any error
            console.log(error);
          })
    }, []);

    console.log('weatherData', weatherData);

    return (
        <div className = "Home">
            <h1>{city}</h1>
            <div className = "WeatherInfo">
                <div className = "WeatherInfo_Image">
                    <img src = "" alt = ""></img>
                </div>
                <div className = "WeatherInfo_Data">

                    <div className = "CurrentTemperature">
                        <p className = "CurrentTemperatureTemp">{weatherData.main && weatherData.main.temp}&#176;</p>
                        <p className = "CurrentTemperatureLabel">Current Temperature</p>
                    </div>

                    <div className = "OtherTemperatures">
                        <p>High Temp: <strong>{weatherData.main && weatherData.main.temp_max}&#176;</strong></p>
                        <p>Low Temp: <strong>{weatherData.main && weatherData.main.temp_min}&#176;</strong></p>
                    </div>

                    <div className = "Stats">
                        <p>Humidity: {weatherData.main && weatherData.main.humidity}%</p>
                        <p>Wind: {weatherData.wind && weatherData.wind.speed}mph</p>
                    </div>
                </div>
                <div>
                    <p>Welcome to {city}, where the current we have {weatherData.weather && weatherData.weather.description}</p>
                </div>
            </div>
        </div>
    )
}

export default Home; // export the single functino called Home
